import React from "react";
import styled from "styled-components";
import "./scroll_style";
const MyScroll = styled.div`
  position: relative;
  z-index: 1;
  bottom: 1.6em;
  display: flex;
  width: 100%;
  justify-content: center;
  background-color: transparent;
  font-weight: bold;
  text-shadow: 1px 1px #45a29e;
  color: #fff;
  .turn {
    cursor: pointer;
    transform: rotate(90deg);
  }
`;
const Scroll_down_component = () => {
  return (
    <>
      <MyScroll>
        <div className='turn'>
          <span> &gt; </span>
        </div>
      </MyScroll>
    </>
  );
};

export default Scroll_down_component;
